def prepare_equipements_data(apps, schema_editor):
    # Valeurs existantes en base
    # ['Aucun', 'Autres', 'BIM', 'LSF', 'Service de communication à distance']
    Accessibilite = apps.get_model("erp", "Accessibilite")
